$(function () {
    "use strict";

    var page = (location.search.match((/page=(\d+)/)) || [])[1] || 1;

    function go(vec) {
        var goto = {back: -1, next: +1}[vec];
        location.href = location.pathname + '?page=' + (page + goto);
    }

    $('[data-action="back"], [data-action="next"]').on('click', function () {
        "use strict";
        go(this.dataset.action);
    });
});

