import {render} from "../../common/json-render";
import Games from '../../twitch/api/Games'

const games = new Games();

const limit = 20;

module.exports = (req, res) => {
    "use strict";

    let page = req.query.page || 1;

    games.request({
        limit: limit,
        offset: limit * Math.max(page - 1, 1)
    }, (err, json)=> {
        render(json);
    });

};
