import {OAUTH} from '../twitch/twitch-config'

export default (req, res)=> {


    let oauthURL = `\
https://api.twitch.tv/kraken/oauth2/authorize?response_type=code\
&client_id=${OAUTH.CLIENT_ID}\
&redirect_uri=${encodeURIComponent(OAUTH.REDIRECT_URI)}\
&scope=${encodeURIComponent(OAUTH.USER_SCOPES)}\
&state=${Math.random()}`;
//&force_verify=true`; // 每次都要授权


    res.setHeader('location', oauthURL);
    res.writeHead(302, {'Content-Type': 'text/html; charset=UTF-8'});
    res.end();

};
