const pugRender = require('../../common/view/pug-render');
const viewIndex = require('path').join(__dirname, './view/index.jade');

module.exports = (req, res) => {
    "use strict";

    pugRender.render(viewIndex, {
        template: {
            title: '直播列表'
        },
        page: {
            liveList: [{
                title: 'test 1'
            },{
                title: 'test 2'
            },{
                title: 'test 3'
            }]
        }
    }, (err, html)=>{
        if (err) {
            res.write(JSON.stringify(err));
        } else {
            res.writeHead(200, {'Content-Type': 'text/html'});
            res.write(html);
        }
        res.end();
    });

};