/**
 * 渲染JSON文件
 * @param {Session} session
 * @param {any} data 要输出的数据
 */
export let render = (session: Session, data: any) => {

    let json = JSON.stringify(data);

    session.response.setHeader('Content-Type', 'application/json; charset=UTF-8');
    //Access-Control-Allow-Origin: http://foo.example
    //Access-Control-Allow-Methods: POST, GET, OPTIONS
    session.response.setHeader('Access-Control-Allow-Origin', '*');
    session.response.setHeader('Access-Control-Allow-Methods', 'POST, GET');
    session.response.writeHead(200);
    session.response.write(json);
    session.response.end();

    // log.debug(`json-render.render(${json})`);

};
