var modActReg = /^[a-z0-9_.-]+$/i;
var enclude = ['api', 'test'];

/**
 * 返回mod_act字符串
 * @param {Object} req request
 */
this.getModAct = function (req) {
    let p = req.params;

    if (p.mod_act)
        return p.mod_act;

    var pathname = req.REQUEST.pathname || '';
    var pathArray = pathname.replace(/^\//, '').split('/');

    p.pathArray = pathArray.slice(1);
    p.mod_act = pathArray.slice(0, 2).join('_');

    return p.mod_act;
};
