"use strict";
module.exports = {
    getModAct: function (req) {
        var p = req.params;
        if (p.mod_act)
            return p.mod_act;
        var pathname = req.REQUEST.pathname || '';
        var pathArray = pathname.replace(/^\//, '').split('/');
        p.pathArray = pathArray.slice(1);
        p.mod_act = pathArray.slice(0, 2).join('_');
        return p.mod_act;
    }
};
