/**
 * 返回mod_act字符串
 * @param {Object} req request
 */
export = {
    getModAct (req) {
        let p = req.params;

        if (p.mod_act)
            return p.mod_act;

        let pathname = req.REQUEST.pathname || '';
        let pathArray = pathname.replace(/^\//, '').split('/');

        p.pathArray = pathArray.slice(1);
        p.mod_act = pathArray.slice(0, 2).join('_');

        return p.mod_act;
    }
}
